mapper.update:
update login set
	lg_no = 2 #{lg_no}, lg_id = #{lg_id}, lg_profile = #{lg_profile}, 
	lg_password = #{lg_password}, lg_email = #{lg_email}, lg_nickname = #{lg_nickname},
	lgfb_no = #{lgfb_no}, lgka_no = #{lgka_no}, m_no = #{m_no},
	lg_login_type = #{lg_login_type}
where m_no = #{m_no};
